import mysql from "mysql2";

// 데이터베이스 연결
const pool = mysql.createPool(
  process.env.JAWSDB_URL ?? {
    host: 'localhost',
    user: 'root',
    database: 'week10',
    password: 'Qnflrlv1*',
    waitForConnections: true,
    connectionLimit: 10,
    queueLimit: 0
  }
);

// async / await 사용
const promisePool = pool.promise();

// getdepartment 등을 정의합니다.
export const selectSql = {
  getUsers : async () => {
    const [rows] = await promisePool.query(`select * from user`);
    
    return rows
  },
  getDepartment : async () => {
    const [rows] = await promisePool.query(`select * from department`);

    return rows
  },
  getLecture : async () => {
    const [rows] = await promisePool.query(`select * from it_lecture`);

    return rows
  }
}

// insert query
export const deleteSql = {
  // data라는 객체 타입의 파라미터에 입력할 정보를 받아서 query문 생성
  deleteDepartment : async (data) => {
    console.log('deleteSql.deleteDepartment:', data.Dnumber);
    const sql = `delete from department where Dnumber=${data.Dnumber}`;
    
    await promisePool.query(sql);
  },
  deleteLecture : async (data) => {
    console.log('deleteSql.deleteIT_Lecture:', data.Lnumber);
    const sql = `delete from it_lecture where Lnumber=${data.Lnumber}`;
    
    await promisePool.query(sql);
  },
}

// update query
export const updateSql = {
  updateEmployee : async () => {
    // where 조건을 만족하는 행에 대해서 salary 수정
    const sql = `update employee set salary = 500 where Minit = "F"`;
    await promisePool.query(sql);
    
  },

  updateDepartment : async (data) => {
    const sql = `update department set dname = "${data.Dname}" where Dnumber = 0`;
    await promisePool.query(sql);

  },
}

